import { createSlice } from "@reduxjs/toolkit";

const initialState = {showCart: false, counter:0, items: []}

const cartSlice = createSlice({
    name: 'cart',
    initialState,
    reducers: {
        toggleCart(state) {
            state.showCart = true;
        }
    }

});

export const cartAction = cartSlice.actions;
export default cartSlice;
